mapper.select:
select r_like, r_unlike
  from like_unlike
 where user_id=#{user_id}
   and review_idx=#{review_idx}

mapper.update:
update like_unlike
set r_like=#{r_like}, r_unlike=#{r_unlike}
where user_id=#{user_id}
  and review_idx=#{review_idx}

update like_unlike
   set r_like=#{r_like}, r_unlike=#{r_unlike}
 where user_id=#{user_id}
   and review_idx=#{review_idx}

update like_unlike
set r_like=#{r_like}, r_unlike=#{r_unlike}
where user_id=#{user_id}
  and review_idx=#{review_idx}

update like_unlike
set r_like=#{r_like}, r_unlike=#{r_unlike}
where user_id=#{user_id}
  and review_idx=#{review_idx}

update like_unlike
   set r_like=#{r_like}, r_unlike=#{r_unlike}
 where user_id=#{user_id}
   and review_idx=#{review_idx}

update like_unlike
set r_like=#{r_like}, r_unlike=#{r_unlike}
where user_id=#{user_id}
  and review_idx=#{review_idx}

mapper.select:
select sum(r_like) r_like, sum(r_unlike) r_unlike
from like_unlike
where review_idx=#{review_idx};
