mapper.select:
select count(user_id)
  from user
 where user_id = #{user_id}


select user_id, auth, u_del_yn
  from user
 where user_id = #{user_id}
 and pw = #{pw}


select pw
  from user
 where user_id = #{user_id}


select user_id
  from user
 where email = #{email}
   and hp = #{hp}

select pw
  from user
 where user_id = #{user_id}
   and hp = #{hp}

mapper.update:
update user
   set pw = #{pw}
 where user_id = #{user_id}
   and hp = #{hp}
